sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    // Fetch the selected user's sys_id from the custom table
    var userId = current.name.toString();

    // Query the sys_user table to get the user's details
    var userGR = new GlideRecord('sys_user');
    if (userGR.get(userId)) {
        // If user found, populate location and email fields in the custom table
        current.location = userGR.location;
        current.email = userGR.email;
    }

})(current, previous);
