sp_rectangle_menu_item.record_script:
var max = 30;
    var t = data;
    t.items = [];
    t.count = 0;

    // Add Record Watchers
    t.record_watchers = [];
    t.record_watchers.push({
        table: 'sysapproval_approver',
        filter: 'group.assignment_group=1e0a1ef0834a0610edb56060ceaad30d^state=requested^approver=' + gs.getUserID()
    });

    var z = new GlideRecord('sysapproval_approver');
    z.addQuery("group.assignment_group", "1e0a1ef0834a0610edb56060ceaad30d");
    z.addQuery("approver", gs.getUserID());
    z.addQuery("state", "requested");
    z.orderByDesc('sys_updated_on');
    z.setLimit(max);
    z.query();

    var link = {};
    link.title = gs.getMessage('View all approvals');
    link.type = 'link';
    link.href = '?id=my_approvals';
    link.items = [];
    t.items.push(link);

    while (z.next()) {
        var a = {};
        var rec = getRecordBeingApproved(z);
        if (!rec.isValidRecord())  // nothing being approved - hmmm
            continue;

        $sp.getRecordValues(a, z, 'sys_id,sys_updated_on');
        a.number = rec.getDisplayValue();
        a.__table = z.getRecordClassName();
        a.short_description = "Approve Donation to " + z.getDisplayValue('document_id.x_1383179_tradenow_charity_list.charity_name') + " Charity";
      	//a.type = 'approval';
        a.type = 'record';
      	a.__page = 'approval_form';
      	//a.href='?id=approval_form';
        t.items.push(a);
        t.count++;
    }

    if (t.count == 1)
        data.badgeHint = gs.getMessage('{0} pending approval', ['' + t.count]);
    else
        data.badgeHint = gs.getMessage('{0} pending approvals', ['' + t.count]);

    function getRecordBeingApproved(gr) {
        if (!gr.sysapproval.nil())
            return gr.sysapproval.getRefRecord();

        return gr.document_id.getRefRecord();
    }

    // Check if ApprovalDelegationUtil is defined and available
    if (typeof ApprovalDelegationUtil === 'undefined' || ApprovalDelegationUtil === null) {
        if (typeof global.ApprovalDelegationUtil !== 'undefined' && global.ApprovalDelegationUtil !== null) {
            var ApprovalDelegationUtil = global.ApprovalDelegationUtil;
        } else {
            // Handle the missing ApprovalDelegationUtil appropriately
            gs.error('ApprovalDelegationUtil is not available.');
            throw new Error('ApprovalDelegationUtil is not available.');
        }
    }
