sp_rectangle_menu_item.record_script:
// only show 30 in header menu dropdown
var max = 30;

var t = data;
t.items = [];
t.count = 0;

// Get the current user ID
var userID = gs.getUserID();

// Use record watchers to tell header when to update dropdown counts
t.record_watchers = [];
//t.record_watchers.push({'table':'incident','filter':'caller_id=' + userID + '^active=true'});
t.record_watchers.push({'table':'x_1383179_tradenow_item_list','filter':'status=reserved'});

// Query the incident table for incidents where the caller is the current user and the incident is active
var z = new GlideRecord('x_1383179_tradenow_item_list');
//z.addActiveQuery();

z.addEncodedQuery('status=reserved');
z.orderByDesc('sys_updated_on');
z.setLimit(max);
z.query();

var link = {};
link.title = gs.getMessage('View all Items');
link.type = 'link';
link.href = '?id=incidents';
link.items = [];
t.items.push(link);

while (z.next()) {
  var a = {};
  a.short_description = z.item_name + "";
  a.number = z.number + "";
  a.sys_id = z.sys_id + "";
  a.sys_updated_on = z.sys_updated_on.getDisplayValue();
  a.__table = z.getRecordClassName();
  a.type = 'record';
  t.items.push(a);
  t.count++;
}

//gs.log('inc: ' + JSON.stringify(t));

if (t.count == 1)
  data.badgeHint = gs.getMessage('{0} updated incident', [''+t.count]);
else
  data.badgeHint = gs.getMessage('{0} updated incidents', [''+t.count]);
