sp_rectangle_menu_item.record_script:
// only show 30 in header menu dropdown
var max = 30;

var t = data;
t.items = [];
t.count = 0;

// Get the current user ID
var userID = gs.getUserID();

// Use record watchers to tell header when to update dropdown counts
t.record_watchers = [];
t.record_watchers.push({
    'table': 'x_1383179_tradenow_item_list',
    'filter': 'status=readypickup^ORstatus=reserved^ORstatus=claimed,notificationflag=false'
});

// Query the custom table for items with the specified statuses updated today
var z = new GlideRecord('x_1383179_tradenow_item_list');
z.addEncodedQuery('status=readypickup^ORstatus=reserved^ORstatus=claimed');
z.addEncodedQuery('sys_updated_onONToday@javascript:gs.beginningOfToday()@javascript:gs.endOfToday()');
z.addQuery('notificationflag',false);
z.orderByDesc('sys_updated_on');
z.setLimit(max);
z.query();

var link = {};
link.title = gs.getMessage('View all notifications');
link.type = 'link';
link.href = '?id=notification';
link.items = [];
t.items.push(link);

while (z.next()) {
    var a = {};
    a.short_description = z.item_name + "";
    if (z.status == 'reserved')
        a.number = "Status: Reserved";
    else if (z.status == 'readypickup')
        a.number = "Status: Ready to Pickup";
    else if (z.status == 'claimed')
        a.number = "Status: Claimed";

    a.sys_id = z.sys_id + "";
    a.sys_updated_on = z.getValue('sys_updated_on');
    a.__table = z.getRecordClassName();
    a.type = 'record';
    a.__page = 'item_details';
    t.items.push(a);
    t.count++;
}

if (t.count == 1)
    data.badgeHint = gs.getMessage('{0} updated notification', ['' + t.count]);
else
    data.badgeHint = gs.getMessage('{0} updated notifications', ['' + t.count]);
