sp_widget.client_script:
api.controller = function($scope, $location) {
	/* widget controller */
	var c = this;

	$scope.charities = [].concat(c.data.items);
	var inputField1 = document.getElementById("charity-search");
	var keywordSearch = function(keyword, productName){
		var length = productName.length;
		for(var i=0; i<=(length-keyword.length); i++){
			if( keyword === productName.slice(i,keyword.length+i)){
				return true;
			}
		}
		return false;
	}

	$scope.handleClear = function(){
		inputField1.value = "";
		$scope.charities = [];
		$scope.charities = [].concat(c.data.items);
	}

	$scope.checkEnter = function(event){
		if(event.keyCode === 13){
			console.log(inputField1.value);
			$scope.charities = [];
			if(inputField1.value == ""){
				console.log(inputField1.value);
				$scope.charities = [].concat(c.data.items);
				console.log($scope.charities);
			} else {
				$scope.charities = [];
				for(var i=0;i<c.data.items.length ;i++){
					var searchTerm = inputField1.value.toLowerCase();
					var product_name = c.data.items[i].name.toLowerCase();
					if(keywordSearch(searchTerm, product_name)){
						$scope.charities.push(c.data.items[i]);
					}
				}
			}
		}
	}


	// Function to handle click event on a charity, navigating to charity's page
	$scope.handleClick = function(charity_id) {
		$location.search("id=charity_page&charity_id=" + charity_id);
	};
};

