sp_widget.client_script:
api.controller=function($scope) {
  /* widget controller */
  var c = this;

	$scope.status = "readypickup";
	var readypick_button = document.getElementById('readypickup');
	var reserved_button = document.getElementById('reserved');
	var my_orders_button = document.getElementById('claimed');

	$scope.handleClick = function(action){
		$scope.status = action;
		if(action === 'readypickup'){
			readypick_button.classList.add('active');
			reserved_button.classList.remove('active');
			my_orders_button.classList.remove('active');
		} else if(action === 'reserved'){
			readypick_button.classList.remove('active');
			reserved_button.classList.add('active');
			my_orders_button.classList.remove('active');
		} else {
			readypick_button.classList.remove('active');
			reserved_button.classList.remove('active');
			my_orders_button.classList.add('active');
		}
	}
};

