sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	function getUserDetails(user){
		var loggedInUser = new GlideRecord('sys_user');
		loggedInUser.get(user);
		data.myname = loggedInUser.getValue('name');
		data.myimage = loggedInUser.getDisplayValue('photo'); 
		data.mypoints = loggedInUser.getValue('u_points');
	}

	getUserDetails(gs.getUserID());

	data.table = 'sys_user';
	data.users = [];
	var userDetails = new GlideRecord(data.table);
	userDetails.orderByDesc('u_points');
	userDetails.setLimit(5);
	userDetails.query();

	var c=1;
	while (userDetails.next()) {
		var obj={
			sys_id:userDetails.getValue('sys_id'),
			name:userDetails.getValue('name'),
			image_url: userDetails.getDisplayValue('photo'),
			points:userDetails.getValue('u_points'),
			rank:c
		};
		data.users.push(obj);
		c++;
	}
	//console.log(data.users);

	var count=1;
	for(var i=0;i<data.users.length;i++){
		if(data.users[i].sys_id!=gs.getUserID()){
			count++;
		}
		else
		{
			//data.mypoints=data.users[i].points;
			//data.myname=data.users[i].name;
			//data.myimage=data.users[i].image_url;
			break;
		}
	}
	data.rank=count;

})();
