sp_widget.client_script:
api.controller = function($scope, spUtil, $http, $location) {
	var c = this;

	// Initialize scope variables
	$scope.currentOption = -1;
	$scope.currentSubOption = 0;
	$scope.availability = $scope.data.options[0][0];
	$scope.optionsMenu = [].concat($scope.data.options[0]);
	$scope.searchKeyword = '';
	$scope.tempSearchKeyword = '';
	$scope.sortOrder = 'creationTime';
	$scope.donationSortOrder = 'donation_amount';
	$scope.donationHistory = [].concat($scope.data.donationHistory);
	$scope.listedItems = [].concat($scope.data.listedItems);
	var tempItemArray=[];
	var modal = document.getElementById('successModal');
	modal.style.display='none';
	var popup = document.getElementById('popup');
	var close = document.getElementById('close');

	$scope.handleItemRelocate = function(sys_id){
		console.log(sys_id);
		$location.search("id=item_details&table=x_1383179_tradenow_item_list&sys_id="+sys_id);
		///tradenow?id=item_details&table=x_1383179_tradenow_item_list&sys_id={{::item.sys_id}}
	}

	$scope.handleCharityRelocate = function(number,sys_id){
		console.log(sys_id);
		console.log(number);
		$location.search("id=charity_page&charity_id=" + number+"&sys_id="+sys_id);
		//$location.search("id=item_details&table=x_1383179_tradenow_item_list&sys_id="+sys_id);
		///tradenow?id=item_details&table=x_1383179_tradenow_item_list&sys_id={{::item.sys_id}}
	}

	$scope.donationHistoryPopUp = function(donation_id, charity_name){
		$scope.data.donation_id = donation_id;
		$scope.data.charity_name = charity_name;
		c.server.update().then(function(response){
			console.log('server updated');
			$scope.data.donation_id = '';
			popup.style.display = "flex";
		});
	}

	$scope.handleClose = function(){
		popup.style.display = "none";
		modal.style.display = "none";
	}

	window.onclick = function(event) {
		console.log(event.target);
		if (event.target != popup) {
			popup.style.display = "none";
			modal.style.display = "none";
		}
	}

	popup.onclick = function(event) {
		console.log('triggering this');
		event.stopPropagation();
	};

	modal.onclick = function(event) {
		console.log('triggering this');
		event.stopPropagation();
	};

	$scope.handleSort = function(){
		if($scope.currentOption === 2){
			if($scope.donationSortOrder === 'donation_amount'){
				$scope.donationSortOrder = '-donation_amount';
				document.getElementById('arrow').style.transform = 'rotate(90deg)';
			} else {
				$scope.donationSortOrder = 'donation_amount';
				document.getElementById('arrow').style.transform = 'rotate(-90deg)';
			}
		} else {
			if($scope.sortOrder === 'creationTime') {
				$scope.sortOrder = '-creationTime';
				document.getElementById('arrow').style.transform = 'rotate(90deg)';
			} else { 
				$scope.sortOrder = 'creationTime'; 
				document.getElementById('arrow').style.transform = 'rotate(-90deg)';
			}
		}
	}


	// Debounce the handleItemClick function using Lodash
	$scope.handleItemClick = function(value) {
		console.log(value);
		$scope.currentOption = value;
		$scope.optionsMenu = [].concat($scope.data.options[value+1]);
		$scope.selectedOption = $scope.optionsMenu[0];
		$scope.currentSubOption = 0;
		$scope.availability = $scope.data.options[value][0];
		$scope.sortOrder = 'creationTime';
		$scope.tempSearchKeyword = '';
		$scope.searchKeyword = '';
		document.getElementById('search-bar').value = '';
		document.getElementById('arrow').style.transform = 'rotate(-90deg)';
	};

	$scope.debouncedHandleItemClick = _.debounce($scope.handleItemClick, 10);

	$scope.onOptionChange = function() {
		console.log($scope.selectedOption);
		var index = $scope.optionsMenu.indexOf($scope.selectedOption);
		console.log(index);
		$scope.currentSubOption = index;
		$scope.availability = $scope.data.options[$scope.currentOption+1][index];
		console.log($scope.availability);
	};


	$scope.searchFilter = function(item) {
		if(!item.donation_amount && $scope.currentOption === 2) {
			console.log('triggering this');
			return false
		}
		if (!$scope.searchKeyword) {
			if(item.donation_amount != '0')
			return true;
		}
		var keyword = $scope.searchKeyword.toLowerCase();
		if($scope.currentOption === -1){
			return item && item.item_name && item.item_name.toLowerCase().includes(keyword);
		}else if ($scope.currentOption === 0) {
			return item && item.item_name && item.item_name.toLowerCase().includes(keyword);
		} else if ($scope.currentOption === 1) {
			return item && item.item_name && item.item_name.toLowerCase().includes(keyword);
		} else if ($scope.currentOption === 2) {
			return item && item.donation_charity_name && item.donation_charity_name.toLowerCase().includes(keyword);
		}
		return false;
	};

	$scope.customListedFilter = function(item){
		if(!item){
			return false;
		}
		if($scope.currentOption === 0){
			if($scope.currentSubOption === 0){
				if(!$scope.searchKeyword) return true;
				else if($scope.searchKeyword && item.item_name.toLowerCase().includes($scope.searchKeyword.toLowerCase())) return true;
			}else if($scope.currentSubOption !==0 && item.availability === $scope.availability){
				if(!$scope.searchKeyword) return true;
				else if($scope.searchKeyword && item.item_name.toLowerCase().includes($scope.searchKeyword.toLowerCase())) return true;
			}
		}
		return false;
	}

	$scope.customOrderFilter = function(item){
		if(!item){
			return false;
		}
		if($scope.currentOption === 1){
			if($scope.currentSubOption === 0){
				if(!$scope.searchKeyword) return true;
				else if($scope.searchKeyword && item.item_name.toLowerCase().includes($scope.searchKeyword.toLowerCase())) return true;
			}else if($scope.currentSubOption !==0 && item.availability === $scope.availability){
				if(!$scope.searchKeyword) return true;
				else if($scope.searchKeyword && item.item_name.toLowerCase().includes($scope.searchKeyword.toLowerCase())) return true;
			}
		}
		return false;
	}

	$scope.customDonationFilter = function(item){
		if(!item){
			return false;
		}
		if($scope.currentOption === 2){
			if(!$scope.searchKeyword) return true;
			else if($scope.searchKeyword && item.donation_charity_name.toLowerCase().includes($scope.searchKeyword.toLowerCase())) return true;
		}
		return false;
	}

	$scope.checkEnterKey = function(event) {
		if (event.keyCode === 13) { // 13 is the Enter key
			$scope.tempSearchKeyword = event.target.value;
			$scope.searchKeyword = $scope.tempSearchKeyword;
		}
	};

	$scope.clearSearch = function(){
		$scope.tempSearchKeyword = "";
		$scope.searchKeyword = $scope.tempSearchKeyword;
		document.getElementById('search-bar').value = '';
	};

	spUtil.recordWatch($scope, 'x_1383179_tradenow_item_list', "statusINavailable,readypickup,reserved,claimed", function(response) {
		//console.log(response.data);
		// statusINavailable,readypickup,reserved,claimed
		c.server.update();
	});

	$scope.handleItemOperation = function(event_name, order_id){
		console.log(event_name);
		if(event_name === 'Confirm Pickup'){
			console.log(order_id);
			$scope.data.action = "Confirm";
			$scope.data.product = order_id;
			c.server.update().then(function(){
				params = [
					'sysparm_query=claimerLIKE'+$scope.data.name,
					'sysparm_fields=sys_id,number,item_name,image,lister_name,claimer,status,sys_updated_on',
					'sysparm_offset=' + 0,
				].join('&');
				console.log(params);
				$http.get('/api/now/table/x_1383179_tradenow_item_list?'+params)
					.then(function(response){
					$scope.data.orderedItems = [];
					$scope.data.reservedItems = [];
					for(var i=0;i<response.data.result.length;i++){
						if(response.data.result[i].status === 'reserved'){
							$scope.data.reservedItems.push(response.data.result[i]);
						} else {
							$scope.data.orderedItems.push(response.data.result[i]);
						}
					}
					for(i=0; i<$scope.data.orderedItems.length;i++){
						if($scope.data.orderedItems[i].status === 'readypickup'){
							$scope.data.orderedItems[i].availability = 'Ready to Pickup';
							$scope.data.orderedItems[i].button = ["Confirm Pickup"];
							$scope.data.orderedItems[i].creationTime = $scope.data.orderedItems[i].sys_updated_on;
						} else if($scope.data.orderedItems[i].status === 'claimed'){
							$scope.data.orderedItems[i].availability = 'Claimed';
							$scope.data.orderedItems[i].creationTime = $scope.data.orderedItems[i].sys_updated_on;
						}
					}
					for(i=0; i<$scope.data.reservedItems.length;i++){
						$scope.data.reservedItems[i].availability = "Reserved";		
						$scope.data.reservedItems[i].button = ["Claim Item","Cancel Reservation"];
						$scope.data.reservedItems[i].creationTime = $scope.data.reservedItems[i].sys_updated_on;
					}
					modal.style.display='block';
				});
			});
		}

		else if(event_name === 'Cancel Reservation'){
			$scope.data.action = "Cancel Reservation";
			$scope.data.product = order_id;
			c.server.update().then(function(){
				params = [
					'sysparm_query=claimerLIKE'+$scope.data.name,
					'sysparm_fields=sys_id,number,item_name,image,lister_name,claimer,status,sys_updated_on',
					'sysparm_offset=' + 0,
				].join('&');
				console.log(params);
				$http.get('/api/now/table/x_1383179_tradenow_item_list?'+params)
					.then(function(response){
					$scope.data.orderedItems = [];
					$scope.data.reservedItems = [];
					for(var i=0;i<response.data.result.length;i++){
						if(response.data.result[i].status === 'reserved'){
							$scope.data.reservedItems.push(response.data.result[i]);
						} else {
							$scope.data.orderedItems.push(response.data.result[i]);
						}
					}
					for(i=0; i<$scope.data.orderedItems.length;i++){
						if($scope.data.orderedItems[i].status === 'readypickup'){
							$scope.data.orderedItems[i].availability = 'Ready to Pickup';
							$scope.data.orderedItems[i].button = ["Confirm Pickup"];
							$scope.data.orderedItems[i].creationTime = $scope.data.orderedItems[i].sys_updated_on;
						} else if($scope.data.orderedItems[i].status === 'claimed'){
							$scope.data.orderedItems[i].availability = 'Claimed';
							$scope.data.orderedItems[i].creationTime = $scope.data.orderedItems[i].sys_updated_on;
						}
					}
					for(i=0; i<$scope.data.reservedItems.length;i++){
						$scope.data.reservedItems[i].availability = "Reserved";		
						$scope.data.reservedItems[i].button = ["Claim Item","Cancel Reservation"];
						$scope.data.reservedItems[i].creationTime = $scope.data.reservedItems[i].sys_updated_on;
					}
				});
			});
		} 

		else if(event_name === 'Claim Item'){
			$scope.data.action = "Claim Item";
			$scope.data.product = order_id;
			c.server.update().then(function(){
				params = [
					'sysparm_query=claimerLIKE'+$scope.data.name,
					'sysparm_fields=sys_id,number,item_name,image,lister_name,claimer,status,sys_updated_on',
					'sysparm_offset=' + 0,
				].join('&');
				console.log(params);
				$http.get('/api/now/table/x_1383179_tradenow_item_list?'+params)
					.then(function(response){
					$scope.data.orderedItems = [];
					$scope.data.reservedItems = [];
					for(var i=0;i<response.data.result.length;i++){
						if(response.data.result[i].status === 'reserved'){
							$scope.data.reservedItems.push(response.data.result[i]);
						} else {
							$scope.data.orderedItems.push(response.data.result[i]);
						}
					}
					for(i=0; i<$scope.data.orderedItems.length;i++){
						if($scope.data.orderedItems[i].status === 'readypickup'){
							$scope.data.orderedItems[i].availability = 'Ready to Pickup';
							$scope.data.orderedItems[i].button = ["Confirm Pickup"];
							$scope.data.orderedItems[i].creationTime = $scope.data.orderedItems[i].sys_updated_on;
						} else if($scope.data.orderedItems[i].status === 'claimed'){
							$scope.data.orderedItems[i].availability = 'Claimed';
							$scope.data.orderedItems[i].creationTime = $scope.data.orderedItems[i].sys_updated_on;
						}
					}
					for(i=0; i<$scope.data.reservedItems.length;i++){
						$scope.data.reservedItems[i].availability = "Reserved";		
						$scope.data.reservedItems[i].button = ["Claim Item","Cancel Reservation"];
						$scope.data.reservedItems[i].creationTime = $scope.data.reservedItems[i].sys_updated_on;
					}
				});
			});
		}
	};

	//Mobile Specific Functions
	$scope.handleSortMobile = function(){
		document.getElementById("sort-dropdown").classList.toggle("show");
	}

	$scope.handleFilterMobile = function(){
		console.log(document.getElementById("filter-dropdown").classList);
		document.getElementById("filter-dropdown").classList.toggle("show");
	}


	// Initial setup
	$scope.selectedOption = $scope.optionsMenu[0];
};

