sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	data.sysUserId = $sp.getParameter("sys_id");
	data.table = 'x_1383179_tradenow_item_list';
	data.donationTable = 'x_1383179_tradenow_m2m_sys_user_x_1383179_tr';
	data.charityTable = 'x_1383179_tradenow_charity_list';
	data.listedItems = [];
	data.orderedItems = [];
	data.donationHistory = [];
	data.donationHistory1 = [];
	data.reservedItems = [];
	data.action = "";
	data.product = "";
	data.options= [['All'],
								 ['All','Available','Claimed','Ready to Pickup'],
								 ['All','Claimed','Ready to Pickup'],
								 ['All']];
	var sysUserGR = new GlideRecord('sys_user');
	var listedItems = new GlideRecord(data.table);
	var orderedItems = new GlideRecord(data.table);
	var donationHistory = new GlideRecord(data.donationTable);
	var donationHistory1 = new GlideAggregate(data.donationTable);
	if(sysUserGR.get(data.sysUserId)){

		data.name = sysUserGR.getValue('name');
		var index = data.name.indexOf(' ');
		data.name = data.name.slice(0,index);

		listedItems.orderBy('number');
		listedItems.addQuery('lister_name','=',data.sysUserId);
		listedItems.query();

		orderedItems.orderBy('sys_created_on');
		orderedItems.addQuery('claimer','=',data.sysUserId);
		orderedItems.query();

		donationHistory.orderByDesc('sys_created_on');
		donationHistory.addQuery('sys_user','=',data.sysUserId);
		donationHistory.query();

		count=1;
		while(listedItems.next()){
			if(listedItems.getValue('status') !== 'reserved'){
				obj = {
					sys_id: listedItems.getValue('sys_id'),
					id: listedItems.getValue('number'),
					item_name: listedItems.getValue('item_name'),
					item_image: listedItems.getDisplayValue('image'),
					item_lister: listedItems.getValue('lister_name'),
					item_claimer: listedItems.getDisplayValue('claimer'),
					item_status: listedItems.getValue('status'),
					creationTime: listedItems.getValue('sys_created_on'),
					availability: ""
				}
				if(obj.item_status === 'readypickup'){
					obj.availability = "Ready to Pickup";
				} else if(obj.item_status === 'claimed'){
					obj.availability = "Claimed";
				} else if(obj.item_status === 'reserved'){
					obj.availability = "Reserved";				
				} else {
					obj.availability = "Available";
				}
				data.listedItems.push(obj);
				count += 1;
			}
		}
		while(orderedItems.next()){
			obj = {
				sys_id: orderedItems.getValue('sys_id'),
				number: orderedItems.getValue('number'),
				item_name: orderedItems.getValue('item_name'),
				image: orderedItems.getValue('image'),
				lister_name: orderedItems.getDisplayValue('lister_name'),
				claimer: orderedItems.getDisplayValue('claimer'),
				status: orderedItems.getValue('status'),
				creationTime: orderedItems.getValue('sys_updated_on'),//correct this
				reservationTime: orderedItems.getValue('reservation_time_ends_at'),
				availability: ""
			}
			if(obj.status === 'readypickup'){
				obj.availability = "Ready to Pickup";
				obj.button = ["Confirm Pickup"];
				data.orderedItems.push(obj);
			} else if(obj.status === 'claimed'){
				obj.availability = "Claimed";
				data.orderedItems.push(obj);
			} else if(obj.status === 'reserved'){
				obj.availability = "Reserved";
				console.log(obj);

				var currentDate = new Date();
				currentDate.setHours(currentDate.getHours()+7);

				var [datePart, timePart] = obj.reservationTime.split(' ')
				var [year, month, day] = datePart.split('-').map(Number);
				var [hours, minutes, seconds] = timePart.split(':').map(Number);
				obj.reservationTime = new Date(year,month-1,day,hours,minutes,seconds);
				obj.currentTime = currentDate;
				obj.timeDifference = (obj.reservationTime.getTime() - currentDate.getTime())/1000;


				obj.button = ["Claim Item","Cancel Reservation"];
				console.log(obj);
				data.reservedItems.push(obj);
			}
		}
		count = 1;
		console.log(data.orderedItems);
		while(donationHistory.next()){
			var charity_code = donationHistory.getDisplayValue('x_1383179_tradenow_charity_list');
			var charityList = new GlideRecord(data.charityTable);
			charityList.addQuery('number','=',charity_code);
			charityList.query();
			var charityName = "";
			var charityImage = "";
			var charitySysId = "";
			var charityNumber = "";
			while(charityList.next()){
				charitySysId = charityList.getValue('sys_id');
				charityName = charityList.getValue('charity_name');
				charityImage = charityList.getDisplayValue('image');
				charityNumber = charityList.getValue('number');
			}

			var flag=false;
			for(var i=0; i<data.donationHistory.length; i++) {
				if(charityName === data.donationHistory[i].donation_charity_name){
					data.donationHistory[i].donation_amount += parseInt(donationHistory.getValue('donation_amount'));
					flag=true;
					break;
				}
			}
			if(flag) continue;
			obj = {
				//donation_transaction_id: donationHistory.getValue('number'),
				donation_charity_number: charityNumber,
				donation_charity_sysId: charitySysId,
				donation_id: donationHistory.getDisplayValue('number'),
				donation_charity_name: charityName,
				donation_charity_code: charity_code,
				donation_person: donationHistory.getDisplayValue('sys_user'),
				donation_amount: parseInt(donationHistory.getValue('donation_amount')),
				creationTime: donationHistory.getValue('sys_created_on'),
				donation_charity_image: charityImage
			}
			data.donationHistory.push(obj);
			count += 1;
		}
	}

	if(input){
		if(input.action === "Confirm"){
			var orderedProducts = new GlideRecord(data.table);
			orderedProducts.addQuery('number',input.product);
			orderedProducts.query();
			if(orderedProducts.next()){
				orderedProducts.status = "claimed";
				orderedProducts.update();
			}
		}

		else if(input.action === 'Cancel Reservation'){
			var orderedProducts = new GlideRecord(data.table);
			orderedProducts.addQuery('number',input.product);
			orderedProducts.query();
			if(orderedProducts.next()){
				orderedProducts.status = "available";
				orderedProducts.update();
			}
		}

		else if(input.action === 'Claim Item'){
			var orderedProducts = new GlideRecord(data.table);
			orderedProducts.addQuery('number',input.product);
			orderedProducts.query();
			if(orderedProducts.next()){
				orderedProducts.status = "readypickup";
				orderedProducts.update();
			}
		}

		else if(input.donation_id){
			console.log(input.donation_id);
			var donationHistorySpecific = new GlideRecord(data.donationTable);
			donationHistorySpecific.orderByDesc('sys_created_on');
			donationHistorySpecific.addQuery('sys_user','=',data.sysUserId);
			donationHistorySpecific.query();
			while(donationHistorySpecific.next()){
				var charitycode = donationHistorySpecific.getDisplayValue('x_1383179_tradenow_charity_list');
				if(charitycode === input.donation_id){
					obj = {
						donation_id: donationHistorySpecific.getDisplayValue('number'),
						donation_person: donationHistorySpecific.getDisplayValue('sys_user'),
						donation_amount: donationHistorySpecific.getDisplayValue('donation_amount'),
						creationTime: donationHistorySpecific.getValue('sys_created_on'),
					}
					data.donationHistory1.push(obj);
				}
			}
			console.log(data.donationHistory1);
		}
	}

})();
