sp_widget.script:
(function($setInterval) {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.table = 'x_1383179_tradenow_charity_list';
	data.items = [];
  var gr = new GlideRecord(data.table);
  gr.query();
	var count=gr.getRowCount();
	var random=Math.floor(Math.random()*count);



//	setInterval(function generateRandomNumber() {
  // var random=Math.floor(Math.random()*count);
//}, 60000);

	//console.log(count+" "+random);
	while (gr.next()) {
		var obj={
			id: gr.getValue('charity_id'),
			name:gr.getValue('charity_name'),
			description:gr.getValue('description'),
			category:gr.getValue('category'),
			raisedAmount: gr.getValue('total_amount_raised'),
			image_url: gr.getDisplayValue('image')
		};
		data.items.push(obj);
    }

	data.random = [];
	data.random.push(data.items[random]);
})();
