sp_widget.script:
(function($setInterval) {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	data.table = 'x_1383179_tradenow_charity_list';
	data.charity = [];
	// Create a new GlideRecord for the charity list table
	var charityGR = new GlideRecord(data.table);
	// Query the table for records
	charityGR.query(); 
	// Get the total number of records
	var count=charityGR.getRowCount();
	// Generate a random number
	var random=Math.floor(Math.random()*count);

	//	setInterval(function generateRandomNumber() {
	// var random=Math.floor(Math.random()*count);
	//}, 60000);

	//console.log(count+" "+random);
	while (charityGR.next()) {
		// Create an object for each record with specific fields
		var obj={
			id: charityGR.getValue('number'),
			name: charityGR.getValue('charity_name'),
			description: charityGR.getValue('description'),
			category: charityGR.getValue('category'),
			raisedAmount: charityGR.getValue('total_amount_raised'),
			image_url: charityGR.getDisplayValue('image')
		};
		// Push the object into the items array
		data.charity.push(obj);
	}
	// Create an array to store the random item
	data.randomCharity = [];
	// Push the randomly selected item into the random array
	data.randomCharity.push(data.charity[random]);
})();
