api.controller = function($scope, spUtil, $element, $window) {
    var c = this;
    $element.hide();
    var height = c.options.height_from_top || 300;
    height = parseInt(height);
    var lastScrollTop = 0;
    var container = angular.element('.sp-scroll');
    var windowElement = angular.element($window);

    var handleScroll = function(e) {
        var currentScroll = angular.element(e.target).scrollTop();

        // Show button when scrolling down and past the defined height
			//previous
			//if (lastScrollTop < currentScroll || lastScrollTop > currentScroll)
        if ((lastScrollTop < currentScroll || lastScrollTop > currentScroll) && currentScroll > height) { 
            $element.show();
        } else {
            $element.hide();
        }
        lastScrollTop = currentScroll;
    };

    container.on("scroll", handleScroll); // For large screen
    windowElement.on("scroll", handleScroll); // For small screens

    $scope.goTop = function() {
        var animationDuration = 300;
        if ($window.scrollY > 0) {
            $('html').animate({
                scrollTop: 0
            }, animationDuration);
        } else {
            spUtil.scrollTo('.sp-scroll', animationDuration);
        }
    };
};

