sp_widget.client_script:
api.controller=function($scope, spUtil) {

	var c=this;
    $scope.emails = [];

    // Fetch emails from the server
    spUtil.get('email_notifications', {}).then(function(response) {
        $scope.emails = response.emails;
    });
}

sp_widget.css:
.email-notifications {
    background-color: #f8f9fa;
    padding: 10px;
    border: 1px solid #dee2e6;
}
.email-notifications ul {
    list-style-type: none;
    padding: 0;
}
.email-notifications li {
    margin-bottom: 10px;
}

sp_widget.script:
(function() {
    data.emails = [];

    var gr = new GlideRecord('sys_email');
    gr.addQuery('type', 'received'); // Filter for received emails
    gr.orderByDesc('sys_created_on'); // Order by creation date
    gr.setLimit(10); // Limit the number of emails retrieved
    gr.query();
    while (gr.next()) {
        var plainTextBody = GlideStringUtil.stripHTML(gr.body.toString());
        data.emails.push({
            subject: gr.subject.toString(),
            body: plainTextBody,
            date: gr.sys_created_on.getDisplayValue()
        });
    }
})();
