sp_widget.script:
(function($sp) {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	var table_name =  options.table || 'x_1383179_tradenow_m2m_sys_user_x_1383179_tr';
	var targetSysId = options.sys_id || '1ba4cee583c34610edb56060ceaad388';

	data.table = $sp.getParameter('table');
	//console.log(table_name);
	//console.log(targetSysId);
	if(table_name){
		var donationDetails = new GlideRecord(table_name);
		if (donationDetails.get(targetSysId)){
			data.donation = {
				transaction_id:donationDetails.getValue('number'),
				donor_name:donationDetails.getDisplayValue('sys_user'),
				charity_name:donationDetails.getDisplayValue('x_1383179_tradenow_charity_list.charity_name'),
				amount:donationDetails.getValue('donation_amount'),
				status:donationDetails.getValue('status'),
				payment_proof: donationDetails.getDisplayValue('payment_proof'),
				time: donationDetails.getValue('sys_created_on'),
			}
			//data.donation.push(obj);
		}
	}
})();
