sp_widget.client_script:
api.controller=function($scope) {
	/* widget controller */
	var c = this;

	/*
	0: Listed Items
	1: Ordered Items
	2: Donation History
	*/
	$scope.currentOption = 0;
	$scope.currentSubOption = 0;
	$scope.availability = $scope.data.options[0][0];
	$scope.listItems = [];
	$scope.listedItemsDiv = document.getElementById('listedItems');
	$scope.orderedItemsDiv = document.getElementById('orderedItems');
	$scope.donationHistoryDiv = document.getElementById('donationHistory');
	var dropDownOption = document.getElementById('selectedOption');


	//Initial Condition

	dropDownOption.onchange = function(){
		var index = this.selectedIndex;
		var inputText = this.children[index].innerHTML.trim();
		$scope.currentSubOption = index;
		$scope.availability = $scope.data.options[$scope.currentOption][$scope.currentSubOption];
		console.log($scope.availability);
	}

	//base values and styles
	$scope.optionsMenu = [].concat($scope.data.options[0]);
	$scope.orderedItemsDiv.style.display='none';
	$scope.donationHistoryDiv.style.display='none';

	//Function to switch between three tabs
	$scope.handleItemClick = function(value){
		$scope.currentOption = value;
		$scope.optionsMenu = [].concat($scope.data.options[value]);
		dropDownOption.selectedIndex = 0;
		if(value === 0){
			$scope.listedItemsDiv.style.display='flex';
			$scope.orderedItemsDiv.style.display='none';
			$scope.donationHistoryDiv.style.display='none'
			$scope.currentOption = 0;
			$scope.availability = $scope.data.options[0][0];
		} else if(value === 1){
			$scope.listedItemsDiv.style.display='none';
			$scope.orderedItemsDiv.style.display='flex';
			$scope.donationHistoryDiv.style.display='none';
			$scope.currentOption = 1;
			$scope.currentSubOption = 0;
			$scope.availability = $scope.data.options[1][0];
		} else{
			$scope.listedItemsDiv.style.display='none';
			$scope.orderedItemsDiv.style.display='none';
			$scope.donationHistoryDiv.style.display='flex';
			$scope.currentOption = 2;
			$scope.currentSubOption = 0;
			$scope.availability = $scope.data.options[2][0];
		}
	}

	};

