sp_widget.client_script:
api.controller=function($scope, $rootScope) {
  /* widget controller */
  var c = this;

	$scope.handleClick=function(widget_name){
		obj={"widget":widget_name};
		$rootScope.$broadcast("item",obj);
	};

};

