sys_script.script:
// This function executes a rule when triggered, updating the total amount raised for a charity record.

(function executeRule(current, previous /*null when async*/ ) {
    // Create a GlideRecord object to query the 'x_1383179_tradenow_charity_list' table
    var userGr = new GlideRecord('x_1383179_tradenow_charity_list');

    // Get the charity record associated with the current record
    userGr.get(current.getValue('x_1383179_tradenow_charity_list'));

    // Retrieve the donation amount from the current record
    var amount = current.donation_amount;

    // Convert the donation amount to a number and divide by 2
    var numberConvert = Number(amount);
    numberConvert = numberConvert / 2;

    // Retrieve the previous total amount raised from the charity record
    var prevAmount = Number(userGr.total_amount_raised);

    // Calculate the new total amount raised by adding half of the donation amount to the previous total
    var newAmount = numberConvert + prevAmount;

    // Update the 'total_amount_raised' field of the charity record with the new total amount
    userGr.total_amount_raised = String(newAmount);
    userGr.update();

})(current, previous);
