sys_script_include.script:
var LocationUtils = Class.create();
LocationUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getLocationDetails: function() {
	var locationSysId = this.getParameter('sys_id');
	var locationDetails = {};

	var locationGR = new GlideRecord('cmn_location'); // Replace with your location table name
	if (locationGR.get(locationSysId)) {
		locationDetails.latitude = locationGR.getValue('latitude');
		locationDetails.longitude = locationGR.getValue('longitude');
	}

	return locationDetails;
},
type: 'LocationUtils'
});
