var ScriptedAutoFill = Class.create();
ScriptedAutoFill.prototype = {
    initialize: function() {
    },
	getcharityorganization: function(){
        var charityGR=new GlideRecord('x_1383179_tradenow_charity_list');
        if(charityGR.get(input.sys_id)){
            var donationGR=new GlideRecord('x_1383179_tradenow_m2m_sys_user_x_1383179_tr');
            donationGR.initialize();
            donationGR.setValue('sys_user', gs.getUserID()); // Set the user ID here
            donationGR.setValue('x_1383179_tradenow_charity_list', input.getUniqueValue());
            donationGR.setValue('donation_amount', input.donation_amount);
            donationGR.insert();
        }
	},
    type: 'ScriptedAutoFill'
};
