sys_script_include.script:
var UserUtils = Class.create();

UserUtils.prototype = {
    initialize: function() {},

    // Method to retrieve user details (location and email) based on the user's sys_id
    getUserDetails: function(userId) {
        var userDetails = {};
        var userGR = new GlideRecord('sys_user');
        if (userGR.get(userId)) {
            userDetails.location = userGR.getValue('location');
            userDetails.email = userGR.getValue('email');
        }
        return userDetails;
    },

    type: 'UserUtils'
};
