sys_script_include.script:
var TradeNowClientUtils = Class.create();
TradeNowClientUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	populateUserPoints: function(){
		var user_id = this.getParameter('sysparm_user_name');
		var userGr = new GlideRecord('sys_user');
		userGr.addQuery('sys_id', user_id);
		userGr.query();
		if(userGr.next()){
			userGr.u_points += 100;
			userGr.update(); 
			return "Points added to user";
		}
		else{
			return "User not found";
		}

	},

    type: 'TradeNowClientUtils'
});
