sys_script_include.script:
/*
This script defines a JavaScript class TradeNowClientUtils that extends the global AbstractAjaxProcessor class.
It provides a method populateUserPoints() to add points to a user based on their sys_id.
*/

var TradeNowClientUtils = Class.create();

// Extend the TradeNowClientUtils prototype with methods from global.AbstractAjaxProcessor
TradeNowClientUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	// Method to populate user points
	populateUserPoints: function(){
		// Retrieve the sys_id of the user from the request parameters
		var user_id = this.getParameter('sysparm_user_name');

		// Create a GlideRecord object to query the 'sys_user' table
		var userGr = new GlideRecord('sys_user');

		// Add a query to find the user with the specified sys_id
		userGr.addQuery('sys_id', user_id);

		// Execute the query
		userGr.query();

		// If a user record is found
		if(userGr.next()){
			// Add 100 points to the user's 'u_points' field
			userGr.u_points += 100;

			// Update the user record
			userGr.update(); 

			// Return a success message
			return "Points added to user";
		}
		else{
			// Return a message indicating that the user was not found
			return "User not found";
		}
	},

    // Define the type of the class
    type: 'TradeNowClientUtils'
});
