sys_ui_action.script:
function confirmPickUp() {
    var URL = "https://dev245733.service-now.com/x_1383179_tradenow_charity_list_list.do?sysparm_userpref_module=7183d2f8830a0610edb56060ceaad36d";
    var gm1 = new GlideModal("glide_ask_standard", false, 600);
    gm1.setPreference("title", "Are you sure you want to confirm the pick up?");
    gm1.setPreference("onPromptComplete", function() {
        var gm = new GlideModal("glide_ask_standard", false, 600);
        gm.setPreference("title", "Do you want to donate?");
        gm.setPreference("onPromptComplete", function() {
            g_navigation.open(URL, "_blank");
            //gsftSubmit(null, g_form.getFormElement(), 'confirm_pick_up');
            g_form.setValue('status', 'claimed');
            g_form.addInfoMessage("Order picked up");
            g_form.save();
        });
        gm.setPreference("onPromptCancel", function() {
            //gsftSubmit(null, g_form.getFormElement(), 'confirm_pick_up');
            g_form.setValue('status', 'claimed');
            g_form.addInfoMessage("Order picked up");
            g_form.save();
        });
        gm.render();
    });
    gm1.render();
}
